// LoginSignup.js
import React, { useState } from 'react';
import './LoginSignup.css';

const LoginSignup = ({setIsLoggedIn}) => {
    const [isLoginSelected, setIsLoginSelected] = useState(true);
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleToggle = () => {
        setIsLoginSelected(!isLoginSelected);
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        if (name === 'username') {
            setUsername(value);
        } else if (name === 'password') {
            setPassword(value);
        }
    };

    const handleFormSubmit = (e) => {
        e.preventDefault();
        if (isLoginSelected) {
            // Implement login logic
            const storedUsername = localStorage.getItem('username');
            const storedPassword = localStorage.getItem('password');
            if (username === storedUsername && password === storedPassword) {
                alert('Login successful');
                setIsLoggedIn(true);
                // Redirect or set logged in state
            } else {
                alert('Invalid username or password');
            }
        } else {
            // Implement signup logic
            localStorage.setItem('username', username);
            localStorage.setItem('password', password);
            alert('Signup successful');
            // Redirect or set logged in state
        }
    };

    return (
        <div className="authPage">
            <div className="toggleButtons">
                <button onClick={handleToggle} className={isLoginSelected ? 'active' : ''}>
                    Login
                </button>
                <button onClick={handleToggle} className={!isLoginSelected ? 'active' : ''}>
                    Signup
                </button>
            </div>


            <form onSubmit={handleFormSubmit} className="authform">
                <input
                    type="text"
                    placeholder="Enter username"
                    name="username"
                    value={username}
                    onChange={handleInputChange}
                />
                <input
                    type="password"
                    placeholder="Enter Password"
                    name="password"
                    value={password}
                    onChange={handleInputChange}
                />
                <button type="submit">{isLoginSelected ? 'Login' : 'Signup'}</button>
            </form>

        </div>
    );
};

export default LoginSignup;
